con:config.script:
def customerid= context.expand('${CreateCustomer#Response#//*:id}')
def mailId= context.expand('${CreateCustomer#Request#//*:email}')
log.info "The customerid  $customerid is for the email  $mailId is created Successfully"
if(mailId!=context.expand('${customerProperties#custMailId}'))
{
	log.info "Invalid Customer Id"
	testRunner.gotoStepByName("deleteCustomer")
}
else{
	log.info "Mail id is $mailId valid customer Id."
}

//Verify customer first name 
def customerid= context.expand('${CreateCustomer#Response#//*:id}')
def cusidfromgetdata=context.expand('${getDataByCustId#Response#//*:id}')
if(customerid==cusidfromgetdata)
{
	if(context.expand('${customerProperties#custFirstName}')==context.expand('${getDataByCustId#Response#//*:firstname}'))
	{
		log.info "Customer First name is verified successfully"
	}else{
		log.info "Customer first name "+context.expand('${getDataByCustId#Response#//*:firstname}')+" is not matching to expected customer firstname" +context.expand('${customerProperties#custFirstName}')
	}

}
else{
	log.info "Actual Customer ID $cusidfromgetdata is not matching expected customer id $customerid."
}

//get the count of the customers with same name
import com.eviware.soapui.support.XmlHolder
def customerList=context.expand('${findlistofCustomer#Response}')
log.info customerList
def holder=new XmlHolder(customerList)
def nodeCount=holder.getNodeValue("count(//result/items/item/id)")
def count=nodeCount.toInteger()
log.info count 
def namecount=0
for(def i=1;i<=count;i++){ 
	def firstname=holder.getNodeValue("//result/items/item["+i+"]/firstname") 
	if(firstname==context.testCase.testSteps['customerProperties'].getPropertyValue("custFirstName"))
	{
		customermailid=holder.getNodeValue("//result/items/item["+i+"]/email")
		namecount++ 
		log.info "Customer $namecount with Mail Id :$customermailid having name $firstname"


	}
}
log.info "Total $namecount customers having same first name"

con:configuration.scriptText:
def result=context.expand('${deleteCustomer#Response#//*:result}')
if(result==true){
	log.info "Customer Id deleted successfully"
}
